import React from "react";

import { Card, Container} from "react-bootstrap";


export default function Home() {
   const [user, setUser] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [hasAccount, setHasAccount] = useState(false);

  const clearInputs = () => {
        setEmail('');
        setPassword('');
    }

    const clearErrors = () =>{
        setEmailError('');
        setPasswordError('')
    }


  const handleSignUp = () => {
      clearErrors();
    firebase
    .auth()
    .createUserInWithEmailAndPassword(email, password)
    .catch((err) => {
        switch(err.code){
        case "auth/email-already-in-use":
        case "auth/invaild-email": 
            setEmailError(err.message);
            break;
        case "auth/weak-password":
            setPasswordError(err.massage);
            break
        }
        });
  };

   const handleSignIn = () => {
    clearErrors();
    firebase
    .auth()
    .signInWithEmailAndPassword(email, password)
    .catch((err) => {
        switch(err.code){
        case "auth/invaild-email": 
        case "auth/user-disabled":
        case "auth/user-not-found":
            setEmailError(err.message);
            break;
        case "auth/wrong-password":
            setPasswordError(err.massage);
            break
        }
        });
  };


    const authListener = () =>{
        firebase.auth().onAuthStateChanged((user) => {
            if(user){
                clearInputs();
                setUser(user);
            }else{
             setUser("");
            }
        });

    };
    useEffect(() => {
        authListener();
    })


	return (
        <Container>
            <div className="homeCard">

                <Card className="text-center">
                <Card.Header><h1>Welcome to Task List!</h1></Card.Header>
                <Card.Body>
                    <Card.Title>Login or Sign Up Below!</Card.Title>
                    <Card.Link href="/signin">Sign In</Card.Link>
                    <Card.Link href="/signup">Sign Up</Card.Link>

                </Card.Body>

                </Card>
            </div>
        </Container>
	);
}
