import React, {useState} from 'react';
import firebase from '../util/firebase';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';

export default function Forms() {
const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));
const classes = useStyles();

    const [title,setTitle] = useState('');
    const handleOnChange = (e) =>{
        setTitle(e.target.value);
    };
    const createTask = () => { 
        const taskRef = firebase.database().ref('Task');
        const task = {
            title,
            complete: false,
        };

        taskRef.push(task);
    };
    return (
        <div>
            <form type='text' onChange={handleOnChange} value={title}><TextField id="outlined-basic" label="R" variant="outlined" /></form>
            <Button variant="contained" color="primary" onClick={createTask}>Add To List</Button>
        </div>
    )
}
